INSERT INTO `regions` VALUES ('east','East','Santhal Pargana Division includes Dumka, Deoghar, Godda, Jamtara, Pakur, and Sahibganj districts. Famous for religious sites and Santhal tribal heritage.','https://images.unsplash.com/photo-1609222057381-2395dd20f6f9?crop=entropy&cs=srgb&fm=jpg&ixid=M3w3NTY2NzR8MHwxfHNlYXJjaHwxfHxqaGFya2hhbmQlMjByZWdpb25zfGVufDB8fHx8MTc1ODE3NTM2OHww&ixlib=rb-4.1.0&q=85','[\"Deoghar Temple\", \"Baidyanath Dham\", \"Santhal Culture\", \"Dumka\", \"Religious Tourism\"]','2025-09-18 06:11:32','2025-09-18 06:11:32'),('north','North','North Chhotanagpur Division includes Hazaribagh, Dhanbad, Bokaro, Giridih, Ramgarh, and Chatra districts. Known for coal mining and wildlife sanctuaries.','https://images.unsplash.com/photo-1583477041518-b244705d89e1?crop=entropy&cs=srgb&fm=jpg&ixid=M3w3NTY2NzR8MHwxfHNlYXJjaHwyfHxqaGFya2hhbmQlMjByZWdpb25zfGVufDB8fHx8MTc1ODE3NTM2OHww&ixlib=rb-4.1.0&q=85','[\"Hazaribagh National Park\", \"Parasnath Hill\", \"Coal Mining Heritage\", \"Betla Wildlife\", \"Dhanbad\"]','2025-09-18 06:11:32','2025-09-18 06:11:32'),('south','South','South Chhotanagpur Division covers Ranchi, Khunti, Gumla, Simdega, and Lohardaga districts. Home to the state capital and scenic waterfalls.','https://images.pexels.com/photos/33587326/pexels-photo-33587326.jpeg','[\"Ranchi Capital\", \"Hundru Falls\", \"Jonha Falls\", \"Rock Garden\", \"Tagore Hill\"]','2025-09-18 06:11:32','2025-09-18 06:11:32'),('west','West','Kolhan Division comprises districts like East Singhbhum, West Singhbhum, and Seraikela-Kharsawan. Known for steel cities, tribal culture, and industrial heritage.','https://images.unsplash.com/photo-1672154702113-b65ccc7b1f0e?crop=entropy&cs=srgb&fm=jpg&ixid=M3w3NTY2NjZ8MHwxfHNlYXJjaHwxfHxqaGFya2hhbmQlMjB0b3VyaXNtfGVufDB8fHx8MTc1ODE3NTM2M3ww&ixlib=rb-4.1.0&q=85','[\"Jamshedpur\", \"Steel City\", \"Dimna Lake\", \"Industrial Heritage\", \"Tribal Culture\"]','2025-09-18 06:11:32','2025-09-18 06:11:32');
